package com.li.struts.action.dean;

import javax.annotation.Resource;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import com.li.service.inters.SubjectServiceInter;
import com.li.struts.form.dean.AccessQuestionForm;

/** 
 * MyEclipse Struts
 * Creation date: 05-28-2014
 * 
 * XDoclet definition:
 * @struts.action
 */
public class AccessQuestionAction extends Action {
	@Resource
	private SubjectServiceInter subjectServiceInter;
	public void setSubjectServiceInter(SubjectServiceInter subjectServiceInter) {
		this.subjectServiceInter = subjectServiceInter;
	}
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		// TODO Auto-generated method stub
		AccessQuestionForm accessQuestionForm = (AccessQuestionForm)form;
		String subjectId_str = accessQuestionForm.getSubjectId();
		int subjectId = Integer.parseInt(subjectId_str);
		
		subjectServiceInter.accessSubject(subjectId);
		
		ActionForward actionForward = new ActionForward();
		actionForward.setPath("/deanUrl.do?flag=deanCheckQuestion");
		
		return actionForward;
	}
}